import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    // 用来存储登录成功之后，得到的token
    token: ''
  },
  getters: {
  },
  mutations: {
    // 更新token的函数
    updataToken (state, newToken) {
      state.token = newToken
    }
  },
  actions: {
  },
  modules: {
  }
})
