import Vue from 'vue'
import Vuex from 'vuex'
import createPersistedState from 'vuex-persistedstate'
Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    // 用来存储登录成功之后，得到的token
    token: ''
  },
  getters: {
  },
  mutations: {
    // 更新token的函数
    updataToken (state, newToken) {
      state.token = newToken
    }
  },
  actions: {
  },
  modules: {
  },
  // 配置为vuex的插件
  plugins: [createPersistedState()]
})
// vuex默认保存再内存中，刷新会清空
